namespace InvoiceGenerator.Common.Constants
{
    public class TemplateConstants
    {
        public const string Logo = "<div id=\"logo\"><img src=\"{{LogoUrl}}\"></div>";
        public const string UserInfoWithLogo = "<div class=\"company\">";
        public const string UserInfoWithoutLogo = "<div class=\"company-no-logo\">";
        public const string Comment = "<div class=\"notice\"><h3>Remarks</h3><div class=\"text-size-fix\"><ul><li>{{Comment}}</li></ul></div>";
        public const string Item = "<tr><td class=\"no\">{{itemNo}}</td><td class=\"desc\">{{itemName}}</td><td class=\"qty\">{{itemQty}}</td><td class=\"unit\">{{itemPrice}}</td><td class=\"final-column total\">{{itemTotalPrice}}</td></tr>";
        public const string BaseHtml = "<!DOCTYPE html><html lang=\"en\"><head><meta charset=\"utf-8\"><title>{{InvoiceNo}}</title><style> #logo {float: left; margin-top: 8px;} #logo img {height: 115px; width:115px} .clearfix:after {content: \"\";display: table;clear: both;} h1 {float: right; text-align: right; margin-top: 50px;} a {color: #0087C3;text-decoration: none;} body { {{PdfPreview}} position: relative;width: 21cm;  max-height: 29.7cm; margin: 0 auto; color: #555555; background: #FFFFFF; font-family: Arial, sans-serif; font-size: 16px;}.company{margin-left: 150px; margin-top: 22px; width: 300px;} .company h2{font-weight: normal; margin: 10px auto;} .company-no-logo {margin-top: 20px;width: 300px;} .company-no-logo h2 {font-size: 1.5em;font-weight: normal;margin: 10px auto;} header .clearfix{padding: 30px 0; border-bottom: 1px solid #AAAAAA;} .text-size-fix{font-size: 1.1em;} .clearfix{padding: 30px 0; border-bottom: 1px solid #AAAAAA;} #client {padding-left: 20px;border-left: 6px solid #0087C3;float: left;} #client .to {font-weight: normal; margin: 0px auto 5px;} #client .name{margin: 0px auto;}#client .company{margin: 0px auto 15px;}#client .address{margin: 0px auto 5px;}#invoice {float: right;text-align: right;}#invoice .date {font-size: 1.1em;color: #777777;}.items-table{max-height: 650px;}table .final-column {border-right: 0px;}table {width: 100%;border-collapse: collapse;border-spacing: 0;margin-bottom: 20px;font-size: 1.1em;}table .no {width: 40px;text-align: center;}table .desc {width: 400px;padding-left: 10px;}table thead {border-bottom: 1px solid #000;}table th {text-align: center;font-size: 1.1em;border-right: 1px solid #000;}table td {border-right: 1px solid #000;border-top: 1px solid #000;padding: 10px 0;}table .unit {width: 100px;text-align: center;}table .qty {width: 100px;text-align: center;}table .total {text-align: center;width: 100px;}#thanks{font-size: 2em;margin-bottom: 50px;}.notice{width: 350px;}.notice h3{background-color: #0087C3;color: #FFFFFF;margin: 0;padding: 6px 20px 5px;border-top-left-radius: 10px;border-top-right-radius: 10px;border-bottom: 1px solid #0087C3;}.notice div{margin: 0;border-bottom-left-radius: 10px;border-bottom-right-radius: 10px;border: 1px solid #0087C3;}.notice ul{margin: 0px;padding: 10px;padding-left: 25px;}.summary{width: 350px;float: right;}.summary h3{background-color: #0087C3;color: #FFFFFF;margin: 0;padding: 6px 10px 5px;border-top-left-radius: 10px;border-top-right-radius: 10px;border-bottom: 1px solid #0087C3;}.summary div{margin: 0;border-bottom-left-radius: 10px;border-bottom-right-radius: 10px;border: 1px solid #0087C3;}.summary div p{font-size: 1.1em;font-weight: bold;margin: 0px;padding: 10px;border-bottom: 1px solid #0087C3;}.title{text-align: left;}.value{float: right;}footer {color: #777777;width: 100%;height: 30px;position: absolute;bottom: 0;border-top: 1px solid #AAAAAA;padding: 8px 0;text-align: center;}</style></head><body><header class=\"clearfix\">{{Logo}}<h1>INVOICE</h1>{{UserInfo}}<h2>{{UserCompanyName}}</h2><div class=\"text-size-fix\">{{UserAddress}}</div><div class=\"text-size-fix\">{{UserContactNumber}}</div><div class=\"text-size-fix\"><a href=\"mailto:{{UserCompanyEmail}}\">{{UserCompanyEmail}}</a></div></div></header><main><div id=\"details\" class=\"clearfix\"><div id=\"client\"><h2 class=\"to\">Invoice To:</h2><h3 class=\"name\">{{ClientName}}</h3><h3 class=\"company\">{{ClientCompanyName}}</h3><div class=\"address\">{{ClientAddress}}</div><div class=\"address\">{{ClientContactNumber}}</div><div><a href=\"mailto:{{ClientEmailAddress}}\">{{ClientEmailAddress}}</a></div></div><div id=\"invoice\"><h2 style=\"margin-top: 0px;\">Invoice No: {{InvoiceNo}}</h2><div class=\"date\">Date of Invoice: {{CreatedDate}}</div><div class=\"date\">Due Date: {{DueDate}}</div></div></div><br><h2 style=\"text-align: center;\">Items / Services</h2><br><div class=\"items-table\"><table><thead><tr><th>#</th><th>Description</th><th>Quantity /<br> Hours</th><th class=\"unit\">Unit Price<br>(AED)</th><th class=\"final-column total\">Total<br>(AED)</th></tr></thead><tbody>{{Item}}</tbody></table></div><br><br><br><div class=\"summary\"><h3>Summary</h3><div><p><span class=\"title\">SubTotal Fee</span><span class=\"value\">AED {{SubTotalFee}}</span></p><p><span class=\"title\">VAT</span><span class=\"value\">% {{VAT}}</span></p><p style=\"border: 0;\"><span class=\"title\">Total Fee</span><span class=\"value\">AED {{TotalFee}}</span></p></div></div>{{Comment}}</main></body></html>";
    }
